// Prepare API queries for 10 Brazilian cities
// Using 15 days lookback for better results

const cities = [
  { name: 'Sao_Paulo', id: '3550308' },
  { name: 'Rio_de_Janeiro', id: '3304557' },
  { name: 'Brasilia', id: '5300108' },
  { name: 'Salvador', id: '2927408' },
  { name: 'Fortaleza', id: '2304400' },
  { name: 'Belo_Horizonte', id: '3106200' },
  { name: 'Manaus', id: '1302603' },
  { name: 'Curitiba', id: '4106902' },
  { name: 'Recife', id: '2611606' },
  { name: 'Porto_Alegre', id: '4314902' }
];

// Calculate date range (15 days back)
const endDate = new Date();
const startDate = new Date();
startDate.setDate(startDate.getDate() - 15);

const published_until = endDate.toISOString().split('T')[0];
const published_since = startDate.toISOString().split('T')[0];

// Prepare queries
const queries = cities.map(city => ({
  city_name: city.name,
  territory_id: city.id,
  published_since: published_since,
  published_until: published_until,
  querystring: 'transporte OR mobilidade OR tr√¢nsito OR ve√≠culo OR √¥nibus OR regula√ß√£o OR passageiros'
}));

return queries.map(q => ({ json: q }));

// Attach city name to API response for aggregation
const cityName = $input.first().json.city_name;
const apiResponse = $input.last().json;

return {
  json: {
    city_name: cityName,
    total_gazettes: apiResponse.total_gazettes || 0,
    gazettes: apiResponse.gazettes || []
  }
};

// Calculate KPIs from aggregated API responses
const items = $input.all();

let totalRegulations = 0;
const activeCities = new Set();
let complianceMentions = 0;
let safetyIncidents = 0;

const cityDetails = [];

for (const item of items) {
  const cityName = item.json.city_name;
  const gazettes = item.json.gazettes || [];
  const count = gazettes.length;

  if (count > 0) {
    activeCities.add(cityName);
    totalRegulations += count;

    cityDetails.push({
      name: cityName.replace(/_/g, ' '),
      regulations: count
    });

    // Count compliance and safety mentions in excerpts
    for (const gazette of gazettes) {
      const excerpts = gazette.excerpts || [];
      for (const excerpt of excerpts) {
        const text = excerpt.toLowerCase();

        // Compliance keywords
        if (text.includes('conformidade') || 
            text.includes('regulamenta√ß√£o') || 
            text.includes('norma') || 
            text.includes('lei')) {
          complianceMentions++;
        }

        // Safety keywords
        if (text.includes('seguran√ßa') || 
            text.includes('acidente') || 
            text.includes('incidente') || 
            text.includes('multa')) {
          safetyIncidents++;
        }
      }
    }
  }
}

// Sort cities by regulation count
cityDetails.sort((a, b) => b.regulations - a.regulations);

return {
  json: {
    kpis: {
      new_regulations: totalRegulations,
      active_municipalities: activeCities.size,
      compliance_mentions: complianceMentions,
      safety_incidents: safetyIncidents
    },
    cities: cityDetails,
    report_date: new Date().toISOString().split('T')[0],
    period_days: 15
  }
};

// Build HTML email using template
const data = $input.first().json;
const kpis = data.kpis;
const cities = data.cities;
const reportDate = data.report_date;
const periodDays = data.period_days;

// Format city badges
const cityBadges = cities.map(city => 
  `<span class="city-badge">${city.name} (${city.regulations})</span>`
).join('');

// Generate insights
const insights = [];
if (kpis.new_regulations > 20) {
  insights.push('üìà Aumento significativo de regulamenta√ß√µes publicadas');
}
if (kpis.active_municipalities >= 7) {
  insights.push('üåü Atividade regulat√≥ria alta em m√∫ltiplos munic√≠pios');
}
if (kpis.safety_incidents > 10) {
  insights.push('‚ö†Ô∏è Aten√ß√£o: M√∫ltiplas men√ß√µes a incidentes de seguran√ßa');
}
if (kpis.compliance_mentions > 15) {
  insights.push('‚úÖ Foco forte em conformidade regulat√≥ria');
}

const insightsHtml = insights.length > 0 
  ? insights.map(i => `<li>${i}</li>`).join('') 
  : '<li>üìä Atividade regulat√≥ria dentro do padr√£o esperado</li>';

const htmlBody = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    body {
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
      background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
      margin: 0;
      padding: 40px 20px;
    }
    .email-container {
      max-width: 700px;
      margin: 0 auto;
      background: white;
      border-radius: 16px;
      box-shadow: 0 20px 60px rgba(0,0,0,0.3);
      overflow: hidden;
    }
    .header {
      background: linear-gradient(135deg, #1e3a8a 0%, #3b82f6 100%);
      color: white;
      padding: 40px 30px;
      text-align: center;
    }
    .header h1 {
      margin: 0 0 10px 0;
      font-size: 32px;
      font-weight: 700;
    }
    .header p {
      margin: 0;
      opacity: 0.9;
      font-size: 16px;
    }
    .content {
      padding: 40px 30px;
    }
    .kpi-grid {
      display: grid;
      grid-template-columns: repeat(2, 1fr);
      gap: 20px;
      margin-bottom: 30px;
    }
    .kpi-card {
      background: linear-gradient(135deg, #f3f4f6 0%, #e5e7eb 100%);
      padding: 25px;
      border-radius: 12px;
      text-align: center;
      border: 2px solid #d1d5db;
      transition: transform 0.2s;
    }
    .kpi-card:hover {
      transform: translateY(-5px);
      box-shadow: 0 10px 20px rgba(0,0,0,0.1);
    }
    .kpi-value {
      font-size: 42px;
      font-weight: 700;
      color: #1e3a8a;
      margin: 10px 0;
    }
    .kpi-label {
      font-size: 14px;
      color: #6b7280;
      text-transform: uppercase;
      letter-spacing: 1px;
      font-weight: 600;
    }
    .section-title {
      font-size: 20px;
      color: #1f2937;
      margin: 30px 0 15px 0;
      font-weight: 600;
      border-bottom: 3px solid #3b82f6;
      padding-bottom: 10px;
    }
    .city-badges {
      display: flex;
      flex-wrap: wrap;
      gap: 10px;
      margin-bottom: 30px;
    }
    .city-badge {
      background: #dbeafe;
      color: #1e40af;
      padding: 8px 16px;
      border-radius: 20px;
      font-size: 13px;
      font-weight: 600;
      border: 1px solid #93c5fd;
    }
    .insights {
      background: #fef3c7;
      border-left: 4px solid #f59e0b;
      padding: 20px;
      border-radius: 8px;
      margin-top: 20px;
    }
    .insights ul {
      margin: 10px 0 0 0;
      padding-left: 20px;
    }
    .insights li {
      margin: 8px 0;
      color: #78350f;
      font-weight: 500;
    }
    .footer {
      background: #f9fafb;
      padding: 25px 30px;
      text-align: center;
      color: #6b7280;
      font-size: 13px;
      border-top: 1px solid #e5e7eb;
    }
    .footer a {
      color: #3b82f6;
      text-decoration: none;
    }
    @media (max-width: 600px) {
      .kpi-grid {
        grid-template-columns: 1fr;
      }
    }
  </style>
</head>
<body>
  <div class="email-container">
    <div class="header">
      <h1>üö¶ Transport KPI Report</h1>
      <p>Brazilian Municipal Regulations ‚Ä¢ ${reportDate}</p>
      <p>Period: Last ${periodDays} days</p>
    </div>

    <div class="content">
      <div class="kpi-grid">
        <div class="kpi-card">
          <div class="kpi-label">New Regulations</div>
          <div class="kpi-value">${kpis.new_regulations}</div>
        </div>
        <div class="kpi-card">
          <div class="kpi-label">Active Municipalities</div>
          <div class="kpi-value">${kpis.active_municipalities}</div>
        </div>
        <div class="kpi-card">
          <div class="kpi-label">Compliance Mentions</div>
          <div class="kpi-value">${kpis.compliance_mentions}</div>
        </div>
        <div class="kpi-card">
          <div class="kpi-label">Safety Incidents</div>
          <div class="kpi-value">${kpis.safety_incidents}</div>
        </div>
      </div>

      <h2 class="section-title">üìç Active Municipalities</h2>
      <div class="city-badges">
        ${cityBadges}
      </div>

      <div class="insights">
        <h3 style="margin: 0 0 10px 0; color: #92400e;">üí° Key Insights</h3>
        <ul>
          ${insightsHtml}
        </ul>
      </div>
    </div>

    <div class="footer">
      <p><strong>Day 14: Transport Regulatory KPIs</strong></p>
      <p>Data source: <a href="https://queridodiario.ok.org.br">Querido Di√°rio</a> (Brazilian Municipal Official Gazettes)</p>
      <p>Automated by n8n ‚Ä¢ Advent Automation 2025</p>
    </div>
  </div>
</body>
</html>
`;

return {
  json: {
    subject: `üö¶ Transport KPI Report - ${reportDate} (${kpis.new_regulations} regulations, ${kpis.active_municipalities} cities)`,
    html: htmlBody,
    kpis: kpis
  }
};

// Log error for debugging
const error = $input.first().json;

console.error('Workflow Error:', error);

return {
  json: {
    error_message: error.message || 'Unknown error',
    error_time: new Date().toISOString(),
    workflow_name: 'Day14_Transport_KPIs_Direct_API'
  }
};
